	if(!$location.match(/\/P\/(.*)/) && !$location.match(/\/L\/(.*)\/M\/(.*)/) ){
		
		if(document.cookie){
		logincomp.fmData()
		}else{
		$connectionAttempted = true
		}
	}
	
})

// make the navbar disappear on clicking on any A and scroll on top of main
	const closePanels = () => {
		menuOpened = false; 
